(642860, 'What caused the incident?', 'Quelle est la cause de l\'incident ?', NULL, NULL, '1.05', 0, 'Select all that apply:', 'Sélectionnez tout ce qui s\'y rapporte :', 0, 0, 0, 0, NULL, 'q-642860', 'mc', 0, 'Human Error, Malicious Attack, Misuse, Not Sure, Not Applicable, Other', 'Erreur humaine, Attaque malveillante, Abus, Pas certain, Non applicable, Autre', '', 634, 1, 399, NULL, NULL, 'yes', 'null', NULL, NULL, NULL, NULL, 0),
(642861, 'What networks were involved in the incident?', 'Quels réseaux ont été impliqués dans l\'incident ?', NULL, NULL, '2.01', 0, 'Select all that apply:', 'Sélectionnez tout ce qui s\'y rapporte :', 0, 0, 0, 0, NULL, 'q-642861', 'mc', 0, 'Internal Network, External Network, Not Sure, Not Applicable, Other', 'Réseau interne, Réseau externe, Pas certain, Non applicable, Autre', '', 635, 1, 399, NULL, NULL, 'yes', 'null', NULL, NULL, NULL, NULL, 0),
(642862, 'What assets were involved in the incident?', 'Quels actifs ont été impliqués dans l\'incident ?', NULL, NULL, '2.02', 0, 'Select all that apply:', 'Sélectionnez tout ce qui s\'y rapporte :', 0, 0, 0, 0, NULL, 'q-642862', 'mc', 0, 'System Software, Application Software, Desktop, Laptop, Server, Mobile Device, File Cabinet, Not Sure, Not Applicable, Other', 'Logiciel système, Logiciel d\'application, Ordinateur de bureau, Ordinateur portable, Serveur, Appareil mobile, Classeur, Pas certain, Non applicable, Autre', '', 635, 1, 399, NULL, NULL, 'yes', 'null', NULL, NULL, NULL, NULL, 0),
