(642865, 'What data was compromised?', 'Quelles types données ont été compromises ?', NULL, NULL, '2.04', 0, NULL, NULL, 0, 0, 0, 0, NULL, 'q-642865', 'qa', 0, '', '', '', 635, 1, 399, NULL, NULL, 'yes', 'null', NULL, NULL, NULL, NULL, 0),
(642866, 'What categories of individuals were affected by the incident?', 'Quelles catégories de personnes ont été touchées par l\'incident ?', NULL, NULL, '2.05', 0, 'Select all that apply:', 'Sélectionnez tout ce qui s\'y rapporte :', 0, 0, 0, 0, NULL, 'q-642866', 'mc', 0, 'Contractors, Customers, Employees, Patients, Prospective Employees, Students, Not Sure, Not Applicable, Other', 'Prestataire, Clients, Employés, Patients, Employés potentiels, Étudiants, Pas certain, Non applicable, Autre', '', 635, 1, 399, NULL, NULL, 'yes', 'null', NULL, NULL, NULL, NULL, 0),
(642867, 'Approximately how many individuals were affected by the incident?', 'Environ combien de personnes ont été touchées par l\'incident ?', NULL, NULL, '2.06', 0, 'If you know the exact number, please select \"other\" and enter it in the space provided.', 'Si vous connaissez le nombre exact, entrez-le dans le champ Commentaire supplémentaire.', 0, 0, 0, 0, NULL, 'q-642867', 'sc', 0, '1 - 10, 10 - 100, 100 - 1000, 1000 - 10000, 10000 - 100000, 100000 - 1 million, 1 million - 10 million, 10 million - 100 million, 100 million +, Not Sure, Not Applicable, Other', '1 - 10, 10 - 100, 100 - 1000, 1000 - 10000, 10000 - 100000, 100000 - 1 million, 1 million - 10 million, 10 million - 100 million, 100 million +, Pas certain, Non applicable, Autre', '', 635, 1, 399, NULL, NULL, 'yes', 'null', NULL, NULL, NULL, NULL, 0),
(642868, 'Approximately how many data records were compromised?', 'Environ combien d\'enregistrements de données ont été compromis ?', NULL, NULL, '2.07', 0, NULL, NULL, 0, 0, 0, 0, NULL, 'q-642868', 'sc', 0, 'Number of compromised data records, Not Sure, Not Applicable', 'Nombre d\'enregistrements de données compromis, Pas certain, Non applicable', '', 635, 1, 399, NULL, NULL, 'yes', 'null', NULL, NULL, NULL, NULL, 0),
